import React, { useEffect, useState } from "react";
import axios from "axios";

function UserList() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ ten: "", email: "", mssv: "", lop: "" });

  // Láº¥y dá»¯ liá»‡u tá»« backend
  useEffect(() => {
    loadUsers();
  }, []);

  const loadUsers = () => {
    axios
      .get("http://localhost:3000/users")
      .then((res) => setUsers(res.data))
      .catch((err) => console.error("Lá»—i khi láº¥y dá»¯ liá»‡u:", err));
  };

  // Xá»­ lÃ½ thÃªm user
  const handleAdd = async () => {
    if (!form.ten || !form.email || !form.mssv || !form.lop) {
      alert("Vui lÃ²ng nháº­p Ä‘áº§y Ä‘á»§ thÃ´ng tin!");
      return;
    }

    try {
      await axios.post("http://localhost:3000/users", form);
      setForm({ ten: "", email: "", mssv: "", lop: "" });
      loadUsers(); // load láº¡i danh sÃ¡ch
    } catch (error) {
      console.error("Lá»—i khi thÃªm user:", error);
    }
  };

  return (
    <div>
      <h2>ðŸ“‹ Danh sÃ¡ch User (MongoDB)</h2>

      <div style={{ marginBottom: "10px" }}>
        <input
          placeholder="TÃªn"
          value={form.ten}
          onChange={(e) => setForm({ ...form, ten: e.target.value })}
        />
        <input
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
        />
        <input
          placeholder="MSSV"
          value={form.mssv}
          onChange={(e) => setForm({ ...form, mssv: e.target.value })}
        />
        <input
          placeholder="Lá»›p"
          value={form.lop}
          onChange={(e) => setForm({ ...form, lop: e.target.value })}
        />
        <button onClick={handleAdd}>âž• ThÃªm</button>
      </div>

      {users.length === 0 ? (
        <p>â€¢ KhÃ´ng cÃ³ user nÃ o</p>
      ) : (
        <table
          border="1"
          cellPadding="8"
          style={{
            borderCollapse: "collapse",
            width: "100%",
            textAlign: "left",
            marginTop: "15px",
          }}
        >
          <thead style={{ backgroundColor: "#f0f0f0" }}>
            <tr>
              <th>TÃªn</th>
              <th>Email</th>
              <th>MSSV</th>
              <th>Lá»›p</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u._id}>
                <td>{u.ten}</td>
                <td>{u.email}</td>
                <td>{u.mssv}</td>
                <td>{u.lop}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default UserList;
