import React, { useEffect, useState } from "react";
import axios from "axios";

function UserList() {
  const [users, setUsers] = useState([]);
  const [newUser, setNewUser] = useState({
    name: "",
    email: "",
    mssv: "",
    lop: ""
  });

  // üü¢ L·∫•y danh s√°ch user khi component load
  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("http://localhost:5000/users");
      setUsers(res.data);
    } catch (err) {
      console.error("‚ùå L·ªói khi l·∫•y d·ªØ li·ªáu:", err);
    }
  };

  // üü¢ Th√™m user m·ªõi
  const addUser = async () => {
    if (!newUser.name || !newUser.email) {
      alert("Vui l√≤ng nh·∫≠p ƒë·∫ßy ƒë·ªß th√¥ng tin!");
      return;
    }
    try {
      await axios.post("http://localhost:5000/users", newUser);
      setNewUser({ name: "", email: "", mssv: "", lop: "" });
      fetchUsers();
    } catch (err) {
      console.error("‚ùå L·ªói khi th√™m user:", err);
    }
  };

  // üü¢ X√≥a user
  const deleteUser = async (id) => {
    if (!window.confirm("B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a user n√†y kh√¥ng?")) return;
    try {
      await axios.delete(`http://localhost:5000/users/${id}`);
      fetchUsers();
    } catch (err) {
      console.error("‚ùå L·ªói khi x√≥a user:", err);
    }
  };

  // üü¢ C·∫≠p nh·∫≠t gi√° tr·ªã nh·∫≠p
  const handleChange = (e) => {
    setNewUser({ ...newUser, [e.target.name]: e.target.value });
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üìã Danh s√°ch User (MongoDB)</h2>

      {/* Form th√™m user */}
      <div style={{ marginBottom: 20 }}>
        <input
          type="text"
          name="name"
          placeholder="H·ªç t√™n"
          value={newUser.name}
          onChange={handleChange}
          style={{ marginRight: 10 }}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={newUser.email}
          onChange={handleChange}
          style={{ marginRight: 10 }}
        />
        <input
          type="text"
          name="mssv"
          placeholder="MSSV"
          value={newUser.mssv}
          onChange={handleChange}
          style={{ marginRight: 10 }}
        />
        <input
          type="text"
          name="lop"
          placeholder="L·ªõp"
          value={newUser.lop}
          onChange={handleChange}
          style={{ marginRight: 10 }}
        />
        <button onClick={addUser}>‚ûï Th√™m</button>
      </div>

      {/* Danh s√°ch user */}
      {users.length === 0 ? (
        <p>‚ö†Ô∏è Hi·ªán ch∆∞a c√≥ user n√†o</p>
      ) : (
        <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
          <thead>
            <tr>
              <th>H·ªç t√™n</th>
              <th>Email</th>
              <th>MSSV</th>
              <th>L·ªõp</th>
              <th>H√†nh ƒë·ªông</th>
            </tr>
          </thead>
          <tbody>
            {users.map((u) => (
              <tr key={u._id}>
                <td>{u.name}</td>
                <td>{u.email}</td>
                <td>{u.mssv}</td>
                <td>{u.lop}</td>
                <td>
                  <button onClick={() => deleteUser(u._id)}>üóëÔ∏è X√≥a</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default UserList;
