import React, { useEffect, useState } from "react";
import API from "../services/api";

function UserList() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({ ten: "", email: "", mssv: "", lop: "" });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const res = await API.get("/users");
    setUsers(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await API.post("/users", form);
    setForm({ ten: "", email: "", mssv: "", lop: "" });
    fetchUsers();
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>Danh sách User (MongoDB)</h2>

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Tên"
          value={form.ten}
          onChange={(e) => setForm({ ...form, ten: e.target.value })}
        />
        <input
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
        />
        <input
          placeholder="MSSV"
          value={form.mssv}
          onChange={(e) => setForm({ ...form, mssv: e.target.value })}
        />
        <input
          placeholder="Lớp"
          value={form.lop}
          onChange={(e) => setForm({ ...form, lop: e.target.value })}
        />
        <button type="submit">Thêm</button>
      </form>

      <ul>
        {users.map((u) => (
          <li key={u._id}>
            {u.ten} - {u.email} - {u.mssv} - {u.lop}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default UserList;
