// frontend/src/components/UserList.jsx
import React, { useEffect, useState } from "react";
import { getUsers, addUser } from "../services/api"; // ‚úÖ d√πng ƒë√∫ng h√†m trong api.js

export default function UserList() {
  const [users, setUsers] = useState([]);
  const [form, setForm] = useState({
    name: "",
    email: "",
    mssv: "",
    lop: "",
  });

  // üü¢ Load to√†n b·ªô user t·ª´ MongoDB
  const loadUsers = async () => {
    try {
      const data = await getUsers(); // ‚úÖ l·∫•y d·ªØ li·ªáu qua h√†m getUsers()
      setUsers(data);
    } catch (err) {
      console.error("‚ùå L·ªói khi load users:", err);
    }
  };

  // üîÑ G·ªçi 1 l·∫ßn khi component render
  useEffect(() => {
    loadUsers();
  }, []);

  // üü£ Th√™m user m·ªõi
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await addUser(form); // ‚úÖ g·ªçi h√†m addUser() trong api.js
      setForm({ name: "", email: "", mssv: "", lop: "" });
      await loadUsers(); // refresh l·∫°i danh s√°ch
    } catch (err) {
      console.error("‚ùå L·ªói khi th√™m user:", err);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üìã Danh s√°ch User (MongoDB)</h2>

      {/* Form th√™m user */}
      <form
        onSubmit={handleSubmit}
        style={{ marginBottom: 20, display: "flex", gap: 10 }}
      >
        <input
          name="name"
          placeholder="T√™n"
          value={form.name}
          onChange={(e) => setForm({ ...form, name: e.target.value })}
        />
        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
        />
        <input
          name="mssv"
          placeholder="MSSV"
          value={form.mssv}
          onChange={(e) => setForm({ ...form, mssv: e.target.value })}
        />
        <input
          name="lop"
          placeholder="L·ªõp"
          value={form.lop}
          onChange={(e) => setForm({ ...form, lop: e.target.value })}
        />
        <button type="submit">‚ûï Th√™m</button>
      </form>

      {/* Danh s√°ch user */}
      <ul>
        {users.length === 0 ? (
          <li>Kh√¥ng c√≥ user n√†o</li>
        ) : (
          users.map((u) => (
            <li key={u._id}>
              <strong>{u.name}</strong> ‚Äî {u.email} ‚Äî {u.mssv} ‚Äî {u.lop}
            </li>
          ))
        )}
      </ul>
    </div>
  );
}
